import React, { useState } from "react";

export default function TextInputDisplay() {
  const [text, setText] = useState("");

  return (
    <div style={{ display: "flex", flexDirection: "column", alignItems: "center", justifyContent: "center", minHeight: "100vh", backgroundColor: "#f3f4f6", padding: "20px" }}>
      <h1 style={{ fontSize: "24px", fontWeight: "bold", marginBottom: "10px" }}>Ingrese Texto</h1>
      <input
        type="text"
        value={text}
        onChange={(e) => setText(e.target.value)}
        style={{ border: "1px solid #ccc", padding: "10px", borderRadius: "5px", width: "250px" }}
        placeholder="Escribe algo aquÃ­..."
      />
      <div style={{ marginTop: "20px", padding: "10px", backgroundColor: "white", boxShadow: "0px 0px 10px rgba(0,0,0,0.1)", borderRadius: "5px", width: "250px", textAlign: "center" }}>
        <p style={{ fontSize: "18px", fontWeight: "500" }}>{text}</p>
      </div>
    </div>
  );
}
