import React, { useState } from "react";

export default function TextInputDisplay() {
  const [text, setText] = useState("");
  const [file, setFile] = useState(null);

  const handleFileChange = (event) => {
    setFile(event.target.files[0]);
  };

  return (
    <div style={{ display: "flex", flexDirection: "column", alignItems: "center", justifyContent: "center", minHeight: "100vh", backgroundColor: "#f3f4f6", padding: "20px" }}>
      <h1 style={{ fontSize: "24px", fontWeight: "bold", marginBottom: "10px" }}>Ingrese el codigo fuente </h1>
      <input
        type="text"
        value={text}
        onChange={(e) => setText(e.target.value)}
        style={{ border: "1px solid #ccc", padding: "10px", borderRadius: "5px", width: "250px", marginBottom: "10px" }}
        placeholder="Escribe aqui el codigo fuente "
      />
      <h1 style={{ fontSize: "24px", fontWeight: "bold", marginBottom: "10px" }}>O carga el archivo del mismo aqui  </h1>
      <input type="file" onChange={handleFileChange} style={{ marginBottom: "20px" }} />
      <div style={{ marginTop: "10px", padding: "10px", backgroundColor: "white", boxShadow: "0px 0px 10px rgba(0,0,0,0.1)", borderRadius: "5px", width: "250px", textAlign: "center" }}>
        <p style={{ fontSize: "18px", fontWeight: "500" }}>{text}</p>
        {file && <p style={{ marginTop: "10px", fontSize: "14px", color: "gray" }}>Archivo seleccionado: {file.name}</p>}
      </div>
    </div>
  );
}
